mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM cetus_bookmark
    WHERE user_uid = #{userUid}
      AND contents_uid = #{contentsUid}
) AS is_wish_exists

mapper.insert:
INSERT INTO cetus_bookmark (
      USER_UID
    , contents_uid
    , REG_UID
    , REG_DT
    , UPDT_UID
    , UPDT_DT
) VALUES (
      #{userUid}
    , #{contentsUid}
    , #{regUid}
    , NOW()
    , #{updtUid}
    , NOW()
)

mapper.delete:
/* cetusBookmark.delete */
DELETE FROM cetus_bookmark
WHERE USER_UID = #{userUid}
  AND contents_uid = #{contentsUid}
