mapper.select:
/* cetusContents.contentPageListCount */
SELECT COUNT(uid)
  FROM cetus_contents
 WHERE workplace_uid = #{workplaceUid}

mapper.update:
/* cetusContents.contentsUpdate */
UPDATE cetus_contents
   SET title = #{title}
	  ,description = #{description}
	  ,contents = #{contents}
	  ,metadata = #{metadata}::jsonb
	  ,sample_data = #{sampleData}::jsonb

	  ,file_uid = #{fileUid}


	  ,thumbnail_uid = #{thumbnailUid}

	  ,updt_uid = #{updtUid}
	  ,updt_dt = NOW()
 WHERE uid = #{uid}

mapper.select:
/* cetusContents.commentRatingAvg */
SELECT ROUND(AVG(CMM.ratings)) AS ROUNDED_AVG_RATING
  FROM CETUS_CONTENTS_COMMENTS CMM
 INNER JOIN CETUS_CONTENTS CC ON CC.UID = CMM.CONTENTS_UID AND CC.WORKPLACE_UID = #{workplaceUid}
 WHERE CMM.CONTENTS_UID = #{contentsUid}

/* cetusContents.commentsPage */ SELECT CMM.UID AS COMMENTS_UID ,CC.UID AS CONTENTS_UID ,CMM.TYPE AS TYPE ,CASE WHEN CMM.TYPE = 'OPINION' THEN '의견' WHEN CMM.TYPE = 'QUESTION' THEN '문의' WHEN CMM.TYPE = 'REPORT' THEN '오류신고' END AS TYPE_STR ,ROUND(CMM.RATINGS) AS RATINGS ,CMM.COMMENT ,CMM.REG_UID ,TO_CHAR(CMM.REG_DT, 'YYYY-MM-DD') AS REG_DT ,CU.USER_NM AS REG_NM ,CF.FILE_ID AS REG_PROFILE_ID FROM CETUS_CONTENTS_COMMENTS CMM INNER JOIN CETUS_CONTENTS CC ON CC.UID = CMM.CONTENTS_UID AND CC.WORKPLACE_UID = #{workplaceUid} LEFT JOIN CETUS_USER CU ON CU.UID = CMM.REG_UID LEFT JOIN CETUS_FILE CF ON CF.FILE_UID = CU.PROFILE_UID AND CF.USE_AT = 'Y' AND CF.SAVED = 'Y' WHERE 1 = 1 AND CC.UID = #{contentsUid}
AND CMM.TYPE = #{type}
ORDER BY CMM.REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

/* cetusContents.commentsCount */
SELECT COUNT(*)
  FROM CETUS_CONTENTS_COMMENTS CMM
 INNER JOIN CETUS_CONTENTS CC ON CC.UID = CMM.CONTENTS_UID AND CC.WORKPLACE_UID = #{workplaceUid}
  LEFT JOIN  CETUS_USER CU ON CU.UID = CMM.REG_UID
  LEFT JOIN CETUS_FILE CF ON CF.FILE_UID = CU.PROFILE_UID AND CF.USE_AT = 'Y' AND CF.SAVED = 'Y'
 WHERE 1 = 1
   AND CC.UID = #{contentsUid}

   AND CMM.TYPE = #{type}

/* cetusContents.findCntByType */
SELECT COUNT(*)
  FROM CETUS_CONTENTS_COMMENTS CMM
 INNER JOIN CETUS_CONTENTS CC ON CC.UID = CMM.CONTENTS_UID AND CC.WORKPLACE_UID = #{workplaceUid}
 WHERE CMM.TYPE = #{type}
   AND CC.UID = #{contentsUid}
