mapper.insert:
/* cetusBbscttAnswer.insert */
INSERT INTO CETUS_BBSCTT_ANSWER (
     BBSCTT_UID
    ,ANSWER_CNT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
    ,DELETE_AT
) VALUES (
     #{bbscttUid}
    ,#{answerCnt}
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
    ,'N'
)

mapper.delete:
/* cetusBbscttAnswer.delete */
UPDATE CETUS_BBSCTT_ANSWER
   SET DELETE_AT = 'Y'
 WHERE ANSWER_UID = #{answerUid}

mapper.select:
/* cetusBbscttAnswer.answerBbsRegPageList */ SELECT CBA.ANSWER_UID ,CBA.BBSCTT_UID ,CBBSCTT.BBSCTT_NM AS BBSCTT_NM ,CBA.ANSWER_CNT ,CBA.REG_UID ,CU.USER_NM AS REG_NM ,CU.USER_ID AS REG_ID ,TO_CHAR(CBA.REG_DT, 'YYYY-MM-DD HH:MM') AS REG_DT FROM CETUS_BBSCTT_ANSWER CBA LEFT JOIN CETUS_USER CU ON CU.UID = CBA.REG_UID INNER JOIN CETUS_BBSCTT CBBSCTT ON CBBSCTT.BBSCTT_UID = CBA.BBSCTT_UID INNER JOIN CETUS_BBS CBBS ON CBBS.BBS_UID = CBBSCTT.BBS_UID WHERE CBBS.BBS_UID = #{bbsUid} AND CBA.DELETE_AT = 'N' AND CBBSCTT.DELETE_AT = 'N'
AND CBA.REG_UID = #{regUid}
ORDER BY CBBSCTT.REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

  /* cetusBbscttAnswer.answerBbsRegPageListCount */
  SELECT COUNT(*)
    FROM CETUS_BBSCTT_ANSWER CBA
    LEFT JOIN CETUS_USER CU ON CU.UID = CBA.REG_UID
   INNER JOIN CETUS_BBSCTT CBBSCTT ON CBBSCTT.BBSCTT_UID = CBA.BBSCTT_UID
   INNER JOIN CETUS_BBS CBBS ON CBBS.BBS_UID = CBBSCTT.BBS_UID
   WHERE CBBS.BBS_UID = #{bbsUid}
     AND CBA.DELETE_AT = 'N'
     AND CBBSCTT.DELETE_AT = 'N'

     AND CBA.REG_UID = #{regUid}

/* cetusBbscttAnswer.answerExcelPageList */ SELECT CBBS.BBS_NM AS BBS_NM ,CASE WHEN CBBS.BBS_TP_CD = 'NOTICE' THEN '공지사항' WHEN CBBS.BBS_TP_CD = 'BOARD' THEN '자유 게시판' WHEN CBBS.BBS_TP_CD = 'FAQ' THEN 'FAQ' WHEN CBBS.BBS_TP_CD = 'QNA' THEN '1:1문의' END AS BBS_TP_CD_NM ,CBBSCTT.BBSCTT_NM AS BBSCTT_NM ,CBA.ANSWER_CNT ,TO_CHAR(CBA.REG_DT, 'YYYY-MM-DD HH:MM') AS REG_DT ,CU.USER_ID || ' (' || CU.USER_NM || ')' AS REG_INFO ,#{bbscttUrlPrefix} || CBBSCTT.BBSCTT_UID AS BBSCTT_URL FROM CETUS_BBSCTT_ANSWER CBA LEFT JOIN CETUS_USER CU ON CU.UID = CBA.REG_UID INNER JOIN CETUS_BBSCTT CBBSCTT ON CBBSCTT.BBSCTT_UID = CBA.BBSCTT_UID INNER JOIN CETUS_BBS CBBS ON CBBS.BBS_UID = CBBSCTT.BBS_UID WHERE CBBS.BBS_UID = #{bbsUid} AND CBA.DELETE_AT = 'N' AND CBBSCTT.DELETE_AT = 'N'
AND CBA.REG_UID = #{regUid}
ORDER BY CBBSCTT.REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

  /* cetusBbscttAnswer.answerExcelPageListCount */
  SELECT COUNT(*)
    FROM CETUS_BBSCTT_ANSWER CBA
    LEFT JOIN CETUS_USER CU ON CU.UID = CBA.REG_UID
   INNER JOIN CETUS_BBSCTT CBBSCTT ON CBBSCTT.BBSCTT_UID = CBA.BBSCTT_UID
   INNER JOIN CETUS_BBS CBBS ON CBBS.BBS_UID = CBBSCTT.BBS_UID
   WHERE CBBS.BBS_UID = #{bbsUid}
     AND CBA.DELETE_AT = 'N'
     AND CBBSCTT.DELETE_AT = 'N'

     AND CBA.REG_UID = #{regUid}

mapper.update:
/* cetusBbscttAnswer.update */
UPDATE CETUS_BBSCTT_ANSWER
   SET ANSWER_CNT = #{answerCnt}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE ANSWER_UID = #{answerUid}
