mapper.insert:
/* cetusBbs.insert */
INSERT INTO CETUS_BBS (
     BBS_NM
    ,BBS_TP_CD
    ,USE_AT
    ,WORKPLACE_UID
    ,BBS_CL_USE_AT
    ,ATCH_AT
    ,ATCH_NUM
    ,UPLOAD_CPCTY
    ,ANSWER_USE_AT
    ,REG_UID
    ,REG_DT
    ,UPDT_UID
    ,UPDT_DT
    ,DELETE_AT
) VALUES (
     #{bbsNm}
    ,#{bbsTpCd}
    ,#{useAt}
    ,#{workplaceUid}
    ,#{bbsClUseAt}
    ,#{atchAt}
    ,#{atchNum}::bigint
    ,#{uploadCpcty}::bigint
    ,#{answerUseAt}
    ,#{regUid}
    ,NOW()
    ,#{updtUid}
    ,NOW()
    ,'N'
)

mapper.select:
/* cetusBbs.bbsList */ SELECT CB.BBS_UID ,CB.BBS_NM ,CB.BBS_TP_CD ,CASE WHEN CB.BBS_TP_CD = 'NOTICE' THEN '공지사항' WHEN CB.BBS_TP_CD = 'BOARD' THEN '자유 게시판' WHEN CB.BBS_TP_CD = 'FAQ' THEN 'FAQ' WHEN CB.BBS_TP_CD = 'QNA' THEN '1:1문의' END AS BBS_TP_CD_NM ,CB.USE_AT ,CB.REG_UID ,TO_CHAR(CB.REG_DT, 'YYYY-MMM-DD') AS REG_DT ,CU.USER_NM AS REG_NM ,CB.WORKPLACE_UID ,CW.NAME AS WORKPLACE_NM ,CB.BBS_CL_USE_AT ,CB.ATCH_AT ,CB.ATCH_NUM ,CB.UPLOAD_CPCTY ,CB.ANSWER_USE_AT ,( SELECT COUNT(*) FROM CETUS_BBSCTT CBBSCTT WHERE CBBSCTT.BBS_UID = CB.BBS_UID AND CBBSCTT.DELETE_AT = 'N' ) AS BBSCTT_CNT FROM CETUS_BBS CB LEFT JOIN CETUS_USER CU ON CU.UID = CB.REG_UID INNER JOIN CETUS_WORKPLACE CW ON CW.UID = CB.WORKPLACE_UID WHERE CB.DELETE_AT = 'N' AND CB.WORKPLACE_UID = #{workplaceUid}
AND CB.USE_AT = #{useAt}
AND (CB.BBS_NM LIKE '%' || #{browseText} || '%' OR CU.USER_NM LIKE '%' || #{browseText} || '%' )
ORDER BY CB.REG_DT DESC
OFFSET ${startNumber} LIMIT ${endNumber}

/* cetusBbs.bbsCount */
SELECT COUNT(*)
  FROM CETUS_BBS CB
  LEFT JOIN CETUS_USER CU ON CU.UID = CB.REG_UID
 INNER JOIN CETUS_WORKPLACE CW ON CW.UID = CB.WORKPLACE_UID
 WHERE CB.DELETE_AT = 'N'
   AND CB.WORKPLACE_UID = #{workplaceUid}

   AND CB.USE_AT = #{useAt}


   AND (CB.BBS_NM LIKE '%' || #{browseText} || '%' OR CU.USER_NM LIKE '%' || #{browseText} || '%' )

mapper.update:
/* cetusBbs.update */
UPDATE CETUS_BBS
   SET BBS_NM = #{bbsNm}
      ,USE_AT = #{useAt}
      ,BBS_CL_USE_AT = #{bbsClUseAt}
      ,ATCH_AT = #{atchAt}
      ,ATCH_NUM = #{atchNum}::bigint
      ,UPLOAD_CPCTY = #{uploadCpcty}::bigint
      ,ANSWER_USE_AT = #{answerUseAt}
      ,UPDT_UID = #{updtUid}
      ,UPDT_DT = NOW()
 WHERE BBS_UID = #{bbsUid}

mapper.delete:
/* cetusBbs.delete */
UPDATE CETUS_BBS
   SET DELETE_AT = 'Y'
 WHERE BBS_UID = #{bbsUid}

mapper.select:
/* cetusBbs.getBbscttCountByBbsUid */
SELECT COUNT(*)
  FROM CETUS_BBSCTT CB
 WHERE CB.BBS_UID = #{bbsUid}
   AND CB.DELETE_AT = 'N'
