mapper.insert:
/* cetusDownloadsHist.insertDownloadsHist */
INSERT INTO CETUS_DOWNLOADS_HIST (
     TARGET_UID
    ,TARGET_CD
    ,USER_UID
    ,FILE_UID
    ,REG_UID
    ,REG_DT
) VALUES (
     #{targetUid}::BIGINT
    ,#{targetCd}
    ,#{userUid}
    ,#{fileUid}
    ,#{regUid}
    ,NOW()
)

mapper.delete:
/* cetusDownloadsHist.deleteDownloadHistUser */
DELETE FROM CETUS_DOWNLOADS_HIST
 WHERE FILE_UID = #{fileUid}
