mapper.insert:
/* cetusFile.insertFile */
INSERT INTO CETUS_FILE (
     FILE_UID
    ,FILE_ID
    ,ORG_FILE_NM
    ,USE_AT
    ,SAVED
    ,DOWN_CNT
    ,REG_DT
    ,REG_ID
) VALUES (
     #{fileUid}
    ,#{fileId}
    ,#{orgFileNm}
    ,'Y'
    ,'N'
    ,0
    ,NOW()
    ,#{regId}
)

mapper.delete:
/* cetusFile.deleteNotDownloadFile */
DELETE FROM CETUS_FILE
 WHERE FILE_UID = #{fileUid}
   AND FILE_ID = '0'
   AND SAVED = 'N'

mapper.update:
/* cetusFile.updateDownloadFile */
UPDATE CETUS_FILE
   SET FILE_ID = #{fileId}
      ,FILE_NM = #{fileNm}
      ,FILE_PATH = #{filePath}
      ,FILE_SIZE = #{fileSize}
      ,EXTENSION = #{extension}
      ,FILE_URL = #{fileUrl}
      ,SAVED = 'Y'
      ,UPDT_DT = NOW()
 WHERE FILE_UID = #{fileUid}
   AND FILE_ID = '0'
   AND SAVED = 'N'

/* cetusFile.updateDownCnt */
UPDATE CETUS_FILE
   SET DOWN_CNT = DOWN_CNT + 1
 WHERE FILE_ID = #{fileId}

mapper.delete:
/* cetusFile.deleteFile */
UPDATE CETUS_FILE
   SET USE_AT = 'N'
      ,UPDT_DT = NOW()
 WHERE FILE_UID = #{fileUid}
