mapper.insert:
/* CetusInvite.insert */
INSERT INTO CETUS_INVITE_CODE (
                         URL
                        ,EMAIL
                       ,EXPIRATION_DATE
                       ,USE_AT
                       ,REG_DT
                       ,UPDT_DT
                       ,REG_UID
                       ,UPDT_UID
) VALUES (
           #{url}
         ,#{email}
         ,#{expirationDate}
         ,#{useAt}
         ,NOW()
         ,NOW()
         ,#{regUid}
         ,#{updtUid}
         )

mapper.update:
/* CetusInvite.activateInvite */
UPDATE CETUS_INVITE_CODE
SET USE_AT = 'Y',
    UPDT_DT = NOW()
WHERE URL = #{token}
