mapper.insert:
/* cetusUserLoginHist.insert */
INSERT INTO CETUS_USER_LOGIN_HIST (
     USER_UID
    ,LOGIN_DT
    ,LOGIN_IP
    ,LOGIN_BROWSER
    ,LOGIN_REGION
    ,LOGIN_ACCESS_URL
    ,SESSION_ID
) VALUES (
     #{userUid}
    ,NOW()
    ,#{loginIp}
    ,#{loginBrowser}
    ,#{loginRegion}
    ,#{loginAccessUrl}
    ,#{sessionId}
)

mapper.select:
/* cetusUserLoginHist.userLoginHistCount */
SELECT COUNT(*)
  FROM CETUS_USER_LOGIN_HIST CULH
 WHERE CULH.USER_UID = #{userUid}

/* cetusUserLoginHist.userLoginHistExcelCount */
SELECT COUNT(*)
  FROM CETUS_USER_LOGIN_HIST CULH
 WHERE CULH.USER_UID = #{userUid}
