mapper.select:
/* file.list */ SELECT FILE_UID , FILE_ID , SAVED , FILE_NM , ORG_FILE_NM , FILE_PATH , FILE_URL , FILE_TYPE , EXTENSION , FILE_SIZE , REG_DT FROM CETUS_FILE WHERE USE_AT = 'Y' AND FILE_UID = #{fileUid} AND SAVED != 'P'
AND SAVED = #{saved}
ORDER BY REG_DT DESC

mapper.insert:
        /* file.insert */
          insert into cetus_file (
			  file_uid
			, file_id
			, org_file_nm
			, file_path
			, file_url
			, file_size
			, file_type
			, extension
			, reg_dt
			, saved
			, reg_id
		  ) values (
              #{fileUid}
            , #{fileId}
            , #{orgFileNm}
            , #{filePath}
            , #{fileUrl}
            , #{fileSize}
            , #{fileType}
            , #{extension}
            , now()
            , #{saved}
            , #{regId}
          )

mapper.update:
        /* file.delete */
        UPDATE CETUS_FILE
           SET USE_AT = 'N'
		      , UPDT_DT = now()
         WHERE FILE_ID = #{fileId}

mapper.delete:
/* file.deleteReal */
DELETE FROM CETUS_FILE
 WHERE FILE_ID = #{fileId}
   
   AND SAVED = #{saved}

mapper.insert:
/* fileLog.insertLog */
INSERT INTO CETUS_FILE_LOG
    (
        FILE_UID
      , FILE_ID
      , WORKER_UID
      , WORKER_NM
      , REG_DT
      , DOWNLOAD_URL
    )
VALUES
    (
        #{fileUid}
      , #{fileId}
      , #{workerUid}
      , #{workerNm}
      , NOW()
      , #{downloadUrl}
    )

mapper.select:
/* booleam.isRegister */
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
  FROM CETUS_FILE
 WHERE FILE_UID = #{fileUid}

mapper.update:
/* file.increaseDownCnt */
UPDATE CETUS_FILE
   SET DOWN_CNT = DOWN_CNT + 1
 WHERE FILE_UID = #{fileUid}
   AND FILE_ID = #{fileId}
